import Form from "../common/form";
import React, { Component } from "react";
import Joi from "joi";
import { getCsdMetadataLinks } from "../services/queryService";


class CSDQuery extends Form {
  state = {
    data: {
      param1: "",
    },
    errors: {},    
    csdQuery: {
      output: ""
    },
    selectedMetadata: ""
  };

  schema = {
    _id: Joi.string(),
    param1: Joi.string()
      .required()
      .label("CSD Param")
  };

 async queryCsd(param1) {
    console.log("about to query the XXX to get metadata links for products which match the input string: " + param1);
    const output = await getCsdMetadataLinks("XXN-MXX1");
    this.setState({csdQuery: {output: output}});
  }


  async getResource() {
    const resourceMetadataUrl = this.state.csdQuery.output;
    console.log("about to query the XXX for product from resource metadata: " + resourceMetadataUrl);
  }

  doSearchForResourceLink = (event) => {
    console.log("searching for resource link "); 
  }


  doReset = (event) => {
    const { param1 } = this.state.data;
    console.log("reseting param1 value '" + param1 + "' ' to blank");

    this.setState({ data: {param1: ""}, csdQuery: {output: ""}  });
  };

  doSubmit = () => {
    const { param1 } = this.state.data;
    this.queryCsd(param1);  
  };

  render() {
    const { data, selectedMetadata, csdQuery } = this.state;

    return (
      <React.Fragment>
          <div className="main-content">
            <form onSubmit={this.handleSubmit} >
              <div className="lower-space">
                <h1>Search for CSD Metadata Links</h1>
              </div>

              <div className="dataBox">
                  {this.renderInput(
                    "param1",
                    "Param1",
                    true,
                    "700px"
                  )}

                  {this.renderButton("Query CSD", "btn btn-primary m-2")}
                  <button id="reset-btn" type="button" className="btn btn-warning m-2" onClick={this.doReset}>Reset</button>
              </div>
            </form>


          <div className="outputBox">
                {this.renderReadOnlyTextArea(
                "data.output",
                "Output",
                false,
                "200px",
                "10",
                csdQuery.output
              )}

          </div>  
        </div>  

      </React.Fragment>
    );
  }
}

export default CSDQuery;
