import Form from "../common/form";
import React, { Component } from "react";
import Joi from "joi";
import { getCsdResourceLink } from "../services/queryService";

class QueryMetadataForResourceLocation extends Form {
    state = {
        data: {
          metadataUrl: "",
        },
        errors: {},    
        metaQueryForResource: {
          resourceUri: ""
        },
      };

      schema = {
        _id: Joi.string(),
        metadataUrl: Joi.string()
          .required()
          .label("Metadata Url")
      };

}

export default QueryMetadataForResourceLocation;
